/* eslint-disable no-unused-vars */
import React, { Component } from 'react'

export default class filter extends Component {
	render() {

		const brands = [...new Set(this.state.products.map(product => product.brand))]


		return (
			<div className="filter">
				<div className="filter-result">{this.props.count} Products</div>
				<div className="filter-sort">
					Sort by {" "}
					<select value={this.props.sort} onChange={this.props.sortProducts}>
						<option value=""></option>	
						<option value="lowest">Lowest price</option>
						<option value="highest">Highest price</option>
						<option value="AZ">Name AZ</option>
						<option value="ZA">Name ZA</option>
					</select>
				</div>
				<div className="filter-brand">
					Brand {" "}
					<select value={this.props.brand} onChange={this.props.filterProducts}>
						<option value="All">All</option>

						{brands.map(brand =>
							<option value={brand}>{brand}</option>
							)}












						{/* <option value="Lancôme">Lancôme</option>
						<option value="Jean Paul Gaultier">Jean Paul Gaultier</option> */}
					</select>
				</div>
				<div className="filter-type">
					Category{" "}
					<select value={this.props.type} onChange={this.props.filterProducts}>
						<option value="All">All</option>
						<option value="Eau de Parfum">Eau de Parfum</option>
						<option value="Eau de Toilette">Eau de Toilette</option>
					</select>
				</div>
			</div>
		)
	}
}
